    <script>
function sendContact() {
	var valid;	
	valid = validateContact();
	if(valid) {
		jQuery.ajax({
		url: "contact_mail.php",
		data:'userName='+$("#userName").val()+'&userEmail='+$("#userEmail").val()+'&subject='+$("#subject").val()+'&content='+$(content).val(),
		type: "POST",
		success:function(data){
		$("#mail-status").html(data);
		},
		error:function (){}
		});
		$("#userName").val("");
		$("#userEmail").val("");
		$("#subject").val("");
		$("#content").val("");
		$("#userName").css('border','');
		$("#userEmail").css('border','');
		$("#subject").css('border','');
		$("#content").css('border','');
		setTimeout(function(){ $("#mail-status").html(''); }, 5000);
	}
}

function validateContact() {
	var valid = true;	
	$(".demoInputBox").css('background-color','');
	$(".info").html('');
	
	if(!$("#userName").val()) {
		$("#userName-info").html("*");
		$("#userName").css('border-color','#26b0ff');
		valid = false;
	}
	if(!$("#userEmail").val()) {
		$("#userEmail-info").html("*");
		$("#userEmail").css('border-color','#26b0ff');
		valid = false;
	}
	if(!$("#userEmail").val().match(/^([\w-\.]+@([\w-]+\.)+[\w-]{2,4})?$/)) {
		$("#userEmail-info").html("Ops*");
		$("#userEmail").css('border-color','#26b0ff');
		valid = false;
	}
	if(!$("#subject").val()) {
		$("#subject-info").html("*");
		$("#subject").css('border-color','#26b0ff');
		valid = false;
	}
	if(!$("#content").val()) {
		$("#content-info").html("*");
		$("#content").css('border-color','#26b0ff');
		valid = false;
	}
	
	return valid;
}
</script>
